INSERT INTO `news` (`id`, `title`, `text`, `date`) VALUES
(1, 'Проведение информационного дня Дорпрофсожа 17.09.2015 на Гомельском узле', 'Визит руководителей и специалистов Республиканского комитета \nпрофсоюза железнодорожников и транспортных строителей на Гомельский \nжелезнодорожный узел Гомельского отделения Белорусской железной дороги \n17.09.2015 года был связан с проведением информационного дня \nДорпрофсожа.\n				<br><p>&nbsp;&nbsp;&nbsp;В рамках информационного дня председателем Дорпрофсожа Рингом\n В.И., его заместителем Вайтеховичем И.А. и главным техническим \nинспектором Новиком А.И. посещались Локомотивное депо Гомель, Гомельская\n дистанция пути  и Гомельский вагонный участок.\n				<br>&nbsp;&nbsp;&nbsp;На предприятиях представители Дорпрофсожа посетили \nпрофсоюзные комитеты, провели встречи с руководителями организаций, \nбеседовали с работниками.\n				<br>&nbsp;&nbsp;&nbsp;В завершении своего пребывания на Гомельском железнодорожном \nузле руководством Дорпрофсожа было проведено совещание с профактивом \nГомельского Райпрофсожа.\n				<br>&nbsp;&nbsp;&nbsp;С положительной стороны была отмечена работа профсоюзных \nкомитетов, их информационная деятельность, проводимые мероприятия по \nизбирательной компании в Республике Беларусь, проведение детского \nлетнего оздоровительного сезона и многое другое.\n			</p>', '2015-09-17'),
(2, 'Заседание Пленума Совета Гомельского областного объединения профсоюзов', '17 декабря 2015 года в заседании Пленума Совета Гомельского \nобластного объединения профсоюзов от Гомельского Райпрофсожа приняли \nучастие члены Совета Гомельского областного объединения профсоюзов:\n				<br><p>Сущинский В.В. – председатель Райпрофсожа;\n				<br>Моисеенко В.Ф. – заместитель председателя Райпрофсожа;\n				<br>Евшук П.В. - председатель первичной организации профсоюза Гомельского вагонного участка;\n				<br>Езерская С.В. - председатель первичной организации профсоюза ЗАО «Гомельский ВСЗ»;\n				<br>Риженков В.И, - председатель первичной организации профсоюза сотрудников УО БелГУТ;\n				<br>Богданова Н.Н. - председатель первичной организации профсоюза Станции Гомель,\n				<br>а также приглашены принять участие в работе Пленума:\n				<br>Козлов В.Г. – начальник ППС Барбаров РУП «Гомельское отделение Белорусской железной дороги»;\n				<br>Малиновская В.В. – бухгалтер, председатель профсоюзного комитета\n ППС Барбаров РУП «Гомельское отделение Белорусской железной дороги».\n				<br>&nbsp;&nbsp;&nbsp;В работе Пленума рассматривались вопросы:\n				<br>1. О внесении изменений в состав Совета Гомельского областного объединения профсоюзов и его Президиума.\n				<br>2. О защите трудовых прав и социально-экономических интересов членов профсоюза в свете реализации решений VII Съезда ФПБ.\n				<br>3. Награждение.\n				<br>&nbsp;&nbsp;&nbsp;Решением Пленума в состав президиума Совета Гомельского \nобластного объединения профсоюзов был введен Сущинский В.В. – \nпредседатель районной организации профсоюза РУП «Гомельское отделение \nБелорусской железной дороги».\n				<br>&nbsp;&nbsp;&nbsp;В завершении работы Пленума председатель Совета Гомельского областного объединения профсоюзов Л.В.Барабанов вручил <b>БЛАГОДАРНОСТЬ за высокий уровень социального партнерства по защите социально-экономических прав и интересов работников</b>\n Козлову В.Г. – начальнику и Малиновской В.В. – бухгалтеру, председателю\n профсоюзного комитета Промывочно-пропарочной станции Барбаров РУП \n«Гомельское отделение Белорусской железной дороги».\n\n			</p>', '2015-12-17'),
(3, 'Городской этап конкурса «Новые имена Беларуси-2016»', 'В соответствии с Постановлением Президиума Совета ФПБ от \r\n24.09.2015 №377 «О проведении Республиканского профсоюзного конкурса \r\nтворчества трудовых коллективов «Новые имена Беларуси-2016».<br><p>\r\n				Райпрофсожем РУП «Гомельское отделение Белорусской железной дороги» была подана заявка на участие в конкурсе.<br>\r\n				В городском этапе конкурса «Новые имена Беларуси-2016», который \r\nпроходил 27.01.2016 года в ГЦК г.Гомеля от Гомельского Райпрофсожа \r\nприняли участие:<br>\r\n				<br>\r\n				&nbsp;&nbsp;&nbsp;1. Сценическое творчество<br></p><ul><li>\r\n				&nbsp;&nbsp;&nbsp;Вокальный ансамбль, в составе:</li><li>\r\n				&nbsp;&nbsp;&nbsp; Бородин Дмитрий Валерьевич – помощник машиниста локомотивного депо Гомель;</li><li>\r\n				&nbsp; &nbsp; Руженков Евгений Викторович – инженер-энергетик КСК;</li><li>\r\n				&nbsp;&nbsp;&nbsp; Кротов Владимир Александрович – машинист тепловоза локомотивного депо Гомель;</li><li>\r\n				&nbsp; &nbsp; Тишкевич Яна Геннадьевна – проводник пассажирских вагонов Гомельского вагонного участка.</li></ul><p>\r\n				<br>\r\n				&nbsp;&nbsp;&nbsp;2.Изобразительное и декоративно-прикладное творчество:<br></p><ul><li>\r\n				&nbsp;&nbsp;&nbsp; Афанасенко Светлана Николаевна – специалист по кадрам Гомельской дистанции пути;</li><li>\r\n				&nbsp;&nbsp;&nbsp; Калиновская Людмила Игоревна – вахтер КСК;</li><li>\r\n				&nbsp;&nbsp;&nbsp; Витебская Ольга Юрьевна – билетный кассир вокзала ст.Гомель;</li><li>\r\n				&nbsp;&nbsp;&nbsp; Тищенко Вера Николаевна – уборщик помещений вокзала ст.Гомель.</li></ul><p>\r\n				&nbsp;&nbsp;&nbsp;По результатам городского этапа конкурса «Новые имена Беларуси-2016» в областной этап конкурса прошли: <br>\r\n				&nbsp;&nbsp;&nbsp;1. Вокальный ансамбль.<br>\r\n				&nbsp;&nbsp;&nbsp;2. Калиновская Л.И.<br>\r\n				&nbsp;&nbsp;&nbsp;3. Витебская О.Ю.<br>\r\n				<br></p><h3>\r\n				&nbsp;&nbsp;&nbsp;<b>Так держать! Дальнейших побед нашим участникам!</b></h3>', '2016-01-27'),
(4, 'Первый профсоюзный туристический форум «Профтур-2016»', ' 28-30 января на базе унитарного предприятия «Гостиничный комплекс\n «Турист» в г.Минске состоялся Первый профсоюзный туристический форум \n«Профтур-2016». ТЭУП «Беларустурист» совместно с УП «Белсоюзкурорт» \nпроводилась выставка-ярмарка туристических услуг. Выставку посетило \nболее 5 000 человек..<br><p>\n				&nbsp;&nbsp;&nbsp;29 января 2016 года с целью более широкого познания предлагаемых \nтуристических услуг по нашей республике выставку-ярмарку посетила группа\n председателей профсоюзных комитетов от Гомельского Райпрофсожа с \nГомельского, Жлобинского и Калинковичского железнодорожных узлов.<br>\n				&nbsp;&nbsp;&nbsp;На выставке всем участникам проводимого мероприятия туристические\n компании предлагали ознакомиться с рядом оказываемых туристических \nуслуг, наличием туристических баз, были представлены туристические \nвозможности всех без исключения районов Беларуси и новые уникальные \nтуристические маршруты. Это интереснейшие факты истории, вековые \nлегенды, неповторимая красота природы. Экскурсии по Беларуси – это также\n отличный вариант доступного и интересного семейного отдыха.<br>\n				&nbsp;&nbsp;&nbsp;Сделан первый и очень важный шаг в возрождении профсоюзного \nтуристического обмена - подписано коммюнике о сотрудничестве профсоюзных\n туроператоров Беларуси и стран ближнего и дальнего зарубежья.</p>', '2016-01-29'),
(5, 'Профсоюзным комитетом был организован тур выходного дня в «Силичи»', ' Профсоюзным комитетом объединенной первичной организации \nпрофсоюза РУП «Гомельское отделение Белорусской железной дороги» \n30.01.2016 года был организован тур выходного дня для 43 членов \nпрофсоюза и их детей в горнолыжный центр «Силичи»', '2016-01-30'),
(6, '15 февраля 2016 года 130 лет образования станции Житковичи', 'Кажется, совсем недавно исполнилось 150 лет Белорусской железной \nдороге. С открытием железнодорожной магистрали, вслед за 150-летней \nдатой, своей значимой летописью были отмечены многие заводы, \nлокомотивные и вагонные депо, станции и вокзалы.<br><p>\n				&nbsp;&nbsp;&nbsp;130 лет назад 15 февраля 1886 года прохождением первого поезда \nначала свою летопись станция Житковичи. В настоящее время станция имеет \nбольшую значимость для экономики региона. Ее услугами на постоянной \nоснове пользуются полтора десятка организаций, есть немало и разовых \nклиентов. За прошлый год железнодорожники отправили 6000 вагонов с \nлесом, зерном, торфобрикетом и другими грузами, приняли 1700 вагонов. На\n пассажирские поезда было продано более 118 тысяч билетов. Хороший старт\n взят и в наступившем году, по сравнению с предыдущим январем объемы \nотгрузки увеличены на 38 процентов. За последние несколько лет \nзначительно преобразилось и здание станции, ставшее архитектурным \nукрашением города, несмотря на то, что построено было сразу после войны.<br>\n				&nbsp;&nbsp;&nbsp;В Житковичах железнодорожную форму с гордостью носят около сотни \nработников – путейцы, составители, дежурные, кассиры, связисты, \nмашинисты. Станция дала немало семейных династий. Как правило, работать \nна магистраль приходят однажды и навсегда. Ее тружеников отличает особая\n ответственность и надежность.<br>\n				&nbsp;&nbsp;&nbsp;В преддверии наступающего юбилея по доброй традиции коллег \nприехало поздравить руководство РУП «Гомельское отделение Белорусской \nжелезной дороги». Первый заместитель начальника Сергей Ярошевич вручил \nлучшим работникам почетные грамоты и благодарности отделения железной \nдороги.  Добрые пожелания коллективу также высказали начальник отдела \nперевозок РУП «Гомельское отделение Белорусской железной дороги» Виктор \nЛистопадов и председатель профсоюзного комитета Сергей Житников.</p>', '2016-02-15'),
(7, 'А, НУ-КА, ДЕВУШКИ!', '18 марта в Гомельском государственном колледже железнодорожного \nтранспорта Белорусской железной дороги состоялся физкультурно-спортивный\n конкурс “А, ну-ка, девушки!”. В программе конкурса были проведены \nэстафеты и спортивный танец с элементами чирлидинга. В эстафетной части \nместа распределились следующим образом:<br><div align="left">&nbsp;1 место – команда “Инкогнито” группа Д-21<br>&nbsp;2 место – команда “Орхидеи” группы И-21, И-11, Р-11<br>&nbsp;3 место – команда “Белые розы” группа Р-21<br>\n				&nbsp;&nbsp;&nbsp;В спортивных танцах с элементами чирлидинга победителем стала \nсборная команда групп Р-21, И-11, И-21, второе место заняла команда \nгруппы Р-31, третье место досталось команде группы Б-11</div>', '2016-03-18'),
(8, 'Акция «Будущее без наркотиков»', '<div align="right"><i><b>«Все наркотики – это пустая трата времени.<br></b></i></div><p align="right"><i><b>\r\n					Они разрушают вашу память, самоуважение, всё, что связано с самолюбием…»</b></i><br>\r\n					<i>Курт Кобейн</i><br>\r\n				</p>\r\n				В течение марта месяца в Гомельском государственном колледже \r\nжелезнодорожного транспорта Белорусской железной дороги проходили \r\nмероприятия направленные на профилактику употребления  наркотических \r\nсредств, психотропных веществ и их аналогов в молодёжной среде. \r\nВолонтёры отряда «Мизерикордия» провели в общежитиях колледжа опрос по \r\nтеме «Правда и вымысел о наркотиках»,  анкетирование среди учащихся \r\n«Твоё отношение к наркотикам», распространили информационные лестовки и \r\nпамятки «Жестокая статистика».<br>\r\n				&nbsp;&nbsp;&nbsp;25 марта состоялась молодёжная акция «Будущее без наркотиков». В \r\nтеатрализованной форме участники акции показали зрителям сломанную \r\nсудьбу молодой девушки, употребляющей наркотики.  По замыслу \r\nорганизаторов акции проведение указанных мероприятий позволило привлечь \r\nширокий круг учащихся к проблеме распространения и употребления \r\nнаркотических и психотропных веществ.<br><br>&nbsp;Заключительным аккордом стали слова произнесённые организаторами акции:<br>\r\n				<b>«Помни! Наркотики и свобода – несовместимы! Это обман, это \r\nкапкан, тупик, яма, это рабство. Быть свободным – значит быть, прежде \r\nвсего, независимым, а это ничего общего с наркотиками не имеет!»</b>', '2016-03-29'),
(9, '«Гомельские мастера-2016»', ' 21 апреля 2016 года в Доме профсоюзов Гомельским областным союзом\n нанимателей и Гомельским областным объединением профсоюзов состоялось \nнаграждение победителей конкурса профессионального мастерства наиболее \nмассовых отраслевых профессий «Гомельские мастера-2016».<br><p>\n				&nbsp;&nbsp;&nbsp;В нынешнем году Райпрофсожем РУП «Гомельское отделение \nБелорусской железной дороги» были представлены следующие работники:<br>\n				&nbsp;&nbsp;&nbsp;<b>Сергеева Екатерина Александровна</b> - билетный кассир вокзала станции Гомель;<br>\n				&nbsp;&nbsp;&nbsp;<b>Мурашкин Александр Павлович</b> - монтер пути Гомельской дистанции пути;<br>\n				&nbsp;&nbsp;&nbsp;<b>Филиппова Марина Анатольевна</b> - дежурный по станции Добруш;<br>\n				&nbsp;&nbsp;&nbsp;<b>Моисейкова Татьяна Васильевна</b> - проводник пассажирских вагонов Гомельского вагонного участка;<br>\n				&nbsp;&nbsp;&nbsp;<b>Семенчук Александр Григорьевич</b> - машинист тепловоза Гомельского локомотивного депо;<br>\n				&nbsp;&nbsp;&nbsp;<b>Адарочкин Александр Олегович</b> – электромеханик Гомельской дистанции сигнализации и связи.<br>\n				&nbsp;&nbsp;&nbsp;Труженики услышали в свой адрес добрые слова за добросовестную \nработу, достигнутые успехи, были награждены нагрудным знаком «Гомельскi \nмайстар», получили дипломы и подарки. </p>', '2016-04-21'),
(10, 'К 30-летию Чернобыльской катастрофы', '<p>В соответствии с Постановлением ФПБ о закреплении шефской помощи \nРеспубликанских отраслевых профсоюзов за детскими учреждениями и \nмногодетными семьями г.Наровли, в рамках благотворительной профсоюзной \nакции, приуроченной к 30-летию Чернобыльской катастрофы, представителями\n от Белорусского профсоюза железнодорожников и транспортных строителей: \nзаместителем председателя Дорпрофсожа Войтеховичем И.А., председателем \nГомельского Райрофсожа Сущинским В.В. и председателем объединенной \nпрофсоюзной организации отделения дороги Житниковым С.А. были посещены \nдетский сад №3 и многодетная семья Полей.<br>\n				&nbsp;&nbsp;&nbsp;В ходе посещения представителями от Белорусского профсоюза \nжелезнодорожников и транспортных строителей детскому саду и многодетной \nсемье были вручены необходимые детям ценные подарки.<br>\n				&nbsp;&nbsp;&nbsp;В ходе встречи дети порадовали своими веселыми песнями и танцами.</p>', '2016-04-25'),
(11, 'Объединительный съезд профсоюзов', 'Дата 18 мая 2016 года останется историческим днем в деятельности \nтрех профсоюзов: Белорусского профсоюза железнодорожников и транспортных\n строителей, Белорусского профсоюза авиационных работников, Белорусского\n профсоюза работников транспорта и дорожного хозяйства, которые на \nОбъединительном съезде профсоюзов, проходившем в зале пленумов Дома \nпрофсоюзов, приняли решение об объединении профсоюзов и создании \nБЕЛОРУССКОГО ПРОФСОЮЗА РАБОТНИКОВ ТРАНСПОРТА И КОММУНИКАЦИЙ \n(БелПрофТранс).<br>\n				&nbsp;&nbsp;&nbsp;В этот же день, до начала открытия объединительного съезда, в \nМинском дворце культуры и спорта железнодорожников состоялась XII \nвнеочередная конференция Белорусского профессионального союза \nжелезнодорожников и транспортных строителей, на которой было принято \nрешение о реорганизации Белорусского профсоюза железнодорожников и \nтранспортных строителей путем объединения (слияния) с Белорусским \nпрофсоюзом работников транспорта и дорожного хозяйства и Белорусским \nпрофсоюзом авиационных работников и об участии представителей \nотраслевого профсоюза в Объединительном съезде трех профсоюзов. Также на\n XII внеочередной конференции были рассмотрены и утверждены проекты \nУстава и Положения вновь созданной ОБЪЕДИНЕННОЙ ОТРАСЛЕВОЙ ПРОФСОЮЗНОЙ \nОРГАНИЗАЦИИ ЖЕЛЕЗНОДОРОЖНИКОВ И ТРАНСПОРТНЫХ СТРОИТЕЛЕЙ, избраны \nделегаты на Объединительный съезд.<br>\n				&nbsp;&nbsp;&nbsp;В соответствии с принятым Уставом отраслевого профсоюза \nизменилось название Районной организации профсоюза РУП «Гомельское \nотделение Белорусской железной дороги»  на <u>Районная территориальная организация профсоюза РУП «Гомельское отделение Белорусской железной дороги»</u> Белорусского профсоюза работников транспорта и коммуникаций.', '2016-05-18'),
(12, 'Уже более 10 лет Профком БелГУТа (студентов) сотрудничает с фондом «Aiutiamoli a vivere»', 'Уже более 10 лет Профком БелГУТа (студентов) сотрудничает с \nИтальянским благотворительным фондом «Поможем им жить». Фонд дважды в \nгод выплачивает стипендии студентам-сиротам. Для моральной поддержки \nтаких ребят фондом регулярно организуются встречи, семинары.<br><p>\n				&nbsp;&nbsp;&nbsp;И в этом году итальянский благотворительный фонд «Aiutiamoli a \nvivere» материально поддержал белорусских студентов, оказавшихся в \nэкстремальных ситуациях без родительской опеки, наградил студентку  \nмагистратуры Бородину Анжелу премией «Лучший студент года».<br>\n				&nbsp;&nbsp;&nbsp;Церемония награждения прошла во время конференции итальянского \nблаготворительного фонда, которая состоялась на базе  Могилёвского \nгосударственного университета имени А.А.Кулешова. В ней приняла участие и\n студенческая делегация БелГУТа в составе 11 человек, стипендиатов этого\n фонда,  возглавляемая председателем студенческого профкома А.А.Красник.<br>\n				&nbsp;&nbsp;&nbsp;Были представлены доклады от всех университетов республики, \nсотрудничающих с итальянским фондом. От нашего университета  с докладом \nна тему «Деятельность студенческих волонтёрских отрядов в помощь \nдетскому дому» выступила студентка группы ГТ-51 Гапутина Мария. Целью \nданной конференции было услышать мнения и предложения вузов по \nдальнейшему усилению сотрудничества с итальянским фондом и развитию в \nреспублике Беларусь волонтёрского движения.<br>\n				&nbsp;&nbsp;&nbsp;В работе конференции приняли участие член Административного \nСовета фонда Лучано Бракони, президент фонда Марцио \nОртолани,вице-президент итальянского фонда «Aiutiamoli a vivere» доктор \nФабрицио Пачифичи.</p>', '2016-05-25'),
(13, 'С 26 по 29 мая было проведено Первенство Белорусской железной дороги по мини-футболу', '<p>\n				В соответствии с Планом проведения физкультурно-оздоровительных  и\n спортивно-массовых мероприятий с участием работников Белорусской \nжелезной дороги в 2016 году, утвержденным приказом от 21.01.2016 №69НЗ, \nна базе Культурно-спортивного комплекса РУП «Гомельское отделение \nБелорусской железной дороги» с 26 по 29 мая было проведено Первенство \nБелорусской железной дороги по мини-футболу среди сборных команд \nотделений дороги.<br>\n				&nbsp;&nbsp;&nbsp;Первенство было проведено на высоком уровне. В ходе соревнований \nкоманда Гомельского отделения железной дороги заняла первое место. \nКоманды, занявшие в Первенстве призовые места, были награждены дипломами\n и командными призами, игроки команд-призеров – памятными сувенирами.</p>', '2016-05-29'),
(15, 'Тэлевіктарына "Размаўляем па-беларуску"', 'Праусаюзны камiтэт БелДУТу прыняў удзел у з''ёмках тэлевектарыны"Размаўляем па-беларуску".<br> У цэнтры ўвагі - спаборніцтва дзвюх каманд у веданні беларускай \r\nмовы і культуры. Асноўная мэта праекта - адрадзіць інтарэс да беларускай\r\n мовы ў нашых суайчыннікаў, прадэманстраваць яе своеасаблівасць сярод \r\nіншых славянскіх моў.<br><p>\r\n				&nbsp;&nbsp;&nbsp;Удзельнікі тэлевіктарыны адказвалi на цікавыя пытанні на веданне \r\nбеларускай мовы, літаратуры, этнаграфіі, мастацтва. Пры гэтым яны \r\nпраяўлялi не толькі свае веды, але і вытрымку, хуткую рэакцыю, \r\nрашучасць, уменне дзейнічаць камандай і будаваць стратэгіі.<br>\r\n				&nbsp;&nbsp;&nbsp;Глядзiце нас у вераснi на «Беларусь 3» будзе цiкава!</p>', '2016-05-31'),
(16, 'Международный туристический слет Белорусского профсоюза железнодорожников и транспортных строителей', 'В целях организации активного отдыха трудящихся и членов их \nсемей, приобщения их к здоровому образу жизни, обмена опытом работы \nпервичных и районных организаций профсоюза в области физической культуры\n и спорта, укрепления международных связей между профсоюзами \nжелезнодорожников, на окраине г.п. Новоельня Дятловского района \nГродненской области с 30 июня по 02 июля 2016 года прошел международный \nтуристический слет Белорусского профсоюза железнодорожников и \nтранспортных строителей с участием команд профсоюзов железнодорожников \nЛитвы, Латвии и России.', '2016-07-02'),
(17, 'Встреча Стромука В. В. с профактивом Гомельского Райпрофсожа', '<p>\n				4 августа в Культурно-спортивном комплексе состоялась встреча \nвновь избранного председателя Объединенной территориальной профсоюзной \nорганизации железнодорожников и транспортных строителей (Дорпрофсож) \nСтромука Валерия Владимировича с профактивом Гомельского Райпрофсожа. На\n встрече обсуждались текущие вопросы  и выполнение предстоящих планов \nработы профсоюзными комитетами Гомельского отделения дороги.<br>\n				&nbsp;&nbsp;&nbsp;Председателю Райпрофсожа Сущинскому В.В. на встрече начальником \nуправления спорта и туризма Гомельского исполкома Езерским Д.В. была \nвручена почетная грамота за оказание постоянной помощи в подготовке \nспортсменов Гомельской области, призеров чемпионатов мира и Европы по \nармрестлингу. \n			</p>', '2016-08-04'),
(18, 'День железнодорожника!', 'В первое воскресенье августа в Республике отмечается День железнодорожника.<br><p>\r\n				4 августа на торжественном мероприятии к профессиональному \r\nпразднику во Дворце культуры железнодорожников заслуженным работникам \r\nотделения дороги были вручены отраслевые награды Управления Белорусской \r\nжелезной дороги и Гомельского отделения дороги, которые соответственно \r\nвручили заместитель начальника Белорусской железной дороги Страмук В.В. и\r\n начальник Гомельского отделения Дворак Г.В.<br>\r\n				&nbsp;&nbsp;&nbsp;Также были вручены почетные грамоты и благодарности от Районной \r\nорганизации профсоюза РУП «Гомельское отделение Белорусской железной \r\nдороги» председателем Гомельского Райпрофсожа Сущинским В.В.<br>\r\n				&nbsp;&nbsp;&nbsp;На праздничном вечере, посвященном Дню железнодорожника в момент \r\nвыступления в центральном фойе Дворца культуры железнодорожников \r\nнародным театром-студией «Классик» была представлена экспозиция живых \r\nскульптур, а по окончании официальной части концертная программа \r\nГомельской филармонии и лучших самодеятельных номеров работников \r\nГомельского отделения дороги.<br>\r\n				&nbsp;&nbsp;&nbsp;В воскресенье, 7 августа в зале игровых видов спорта спортивного \r\nкомплекса «Локомотив» состоялись соревнования среди коллективов \r\nпредприятий Гомельского отделения дороги по минифутболу. В состоявшемся \r\nтурнире победителем стала команда Гомельского вагонного депо.<br>\r\n				&nbsp;&nbsp;&nbsp;По окончании спортивных мероприятий Культурно-спортивным комплексом так же была подготовлена концертная программа. \r\n			</p>', '2016-08-07'),
(19, 'Туристический слёт Гомельских железнодорожников', ' На базе детского оздоровительного лагеря «Лесные дали» с19 по 21 \nавгуста 2016 года прошел Туристический слёт организаций и предприятий, \nвходящих в структуру Районной организации профессионального союза \nжелезнодорожников и транспортных строителей РУП «Гомельское отделение \nБелорусской железной дороги» (Райпрофсожа) в программе Круглогодичной \nспартакиады, посвященного 80-летию физкультурно-спортивного движения \nжелезнодорожников. Соревнования пятнадцати команд проводились с целью \nпропаганды здорового образа жизни, популяризации активных форм отдыха \nсреди железнодорожников, дальнейшего привлечения работников предприятий \nотделения к физической культуре и спорту, выявления сильнейших \nспортсменов для участия в Дорожных соревнованиях.<br><p>\n				&nbsp;&nbsp;&nbsp;Организаторами была представлена насыщенная программа проведения \nтуристического слета, которая давала возможность показать участникам, не\n только хорошую спортивную подготовку, но и продемонстрировать свои \nтворческие таланты.<br>\n				&nbsp;&nbsp;&nbsp;По итогам всех конкурсов и соревнований сборная команда \nГомельского ВСЗ заняла первое место.  На втором месте расположилась \nкоманда Локомотивного депо Гомель. Третье место заняла команда \nГомельской дистанции сигнализации и связи.<br>\n				&nbsp;&nbsp;&nbsp;Приятные моменты отличного турслёта ещё долго будут вспоминаться \nего участниками, оставив самые лучшие эмоции и яркие впечатления.<br>\n				&nbsp;&nbsp;&nbsp;Поздравляем победителей и призёров туристических конкурсов и соревнований!</p>', '2016-08-21'),
(20, 'Общественный контроль в работе городских мобильных рейдовых групп', ' За 7 месяцев 2016 года в строительных организациях Гомельской \nобласти погибло в 2 раза больше человек, чем в аналогичный период \nпрошлого года. Растет травматизм и с тяжелым исходом. Основными \nпричинами смерти и травматизма на производстве продолжают оставаться \nнарушения трудовой и производственной дисциплины, правил охраны труда, а\n так же невыполнение должностных обязанностей ответственными лицами \nнанимателя.<br><p>\n				&nbsp;&nbsp;&nbsp;Во исполнение п. 1.6 протокола заседания комиссии по профилактике\n травматизма и профессиональной заболеваемости при Гомельском \nоблисполкоме, сентябрь 2016 года объявлен месяцем безопасности в \nстроительных организациях области.<br>\n				&nbsp;&nbsp;&nbsp;В преддверии месячника, 31 августа мобильная рейдовая группа \nадминистрации Железнодорожного района г.Гомеля с целью мониторинга \nсостояния охраны труда, производственной и трудовой дисциплины при \nучастии технического инспектора труда Районной организации профсоюза РУП\n «Гомельское отделение Белорусской железной дороги» Игоря Гулака, \nпосетила ряд строительных объектов Железнодорожного района города. В \nходе рейда был осуществлен общественный контроль за соблюдением \nзаконодательства об охране труда в части создания на строительных \nобъектах здоровых и безопасных условий труда, пожарной безопасности и \nсанитарного состояния, обеспечения работников бытовыми условиями, \nспецодеждой, спецобувью и необходимыми СИЗ, а так же их применением, \nвыполнение требований Директивы Президента Республики Беларусь от 11 \nмарта 2004 г. № 1 «О мерах по укреплению общественной безопасности и \nдисциплины», с учетом изменений и дополнений, внесенных Указом \nПрезидента Республики Беларусь от 12 октября 2015 г. № 420.<br>\n				&nbsp;&nbsp;&nbsp;Остается на низком уровне организация быта работающих на \nстроительных объектах, контроль обеспечения и применения спецодежды, \nспецобуви и необходимых СИЗ работающими, исполнение требований \nзаконодательства об охране труда, организация предсменного контроля, \nсоблюдение режима курения на объектах. Вместе с тем, в ходе работы \nмобильной группы, в состоянии алкогольного, токсического либо \nнаркотического опьянения работников выявлено не было. По результатам \nрейда нанимателям выданы рекомендации по устранению выявленных \nнарушений.</p>', '2016-09-01'),
(21, 'С ПРАЗДНИКОМ ЗНАНИЙ!!! ВСТУПАЙ В ПРОФСОЮЗ!!!', ' 1 сентября 2016 года в Белорусском государственном университете \nтранспорта, Гомельском колледже-филиале Белорусского государственного \nуниверситета транспорта, Гомельском государственном профессиональном \nлицее железнодорожного транспорта состоялись торжественные линейки, \nпосвященные Дню знаний.<br><p>\n				&nbsp;&nbsp;&nbsp;С приветственными словами к первокурсникам, учащимся, гостям и \nпедагогическим коллективам обратились представители администрации \nучебных заведений, ветераны труда, бывшие выпускники учебных заведений, \nпредставители базовых предприятий железной дороги. Они поздравили всех с\n праздником – Днем знаний и пожелали учащимся успехов в учебе, \nпедагогическому коллективу – творческих свершений, благополучия, \nвыразили слова благодарности за ежедневный кропотливый труд, \nпрофессионализм и мудрость.<br>\n				&nbsp;&nbsp;&nbsp;Теплыми словами поздравил присутствующих и приглашенный на \nлинейку выпускник колледжа 1980 года - председатель Районной организации\n профсоюза РУП «Гомельское отделение Белорусской железной дороги» \nСущинский Виталий Валентинович и вручил памятные подарки лучшим учащимся\n колледжа по итогам 2015-2016 учебного года.<br>\n				&nbsp;&nbsp;&nbsp;После линейки колледжа состоялось организационное собрание для \nгрупп нового набора и председатель профкома учащихся Редюк Татьяна \nАнатольевна провела акцию «Вступай в профсоюз!!!», по итогам которой все\n учащиеся первого курса вступили в большую семью Белорусского \nпрофессионального союза работников транспорта и коммуникаций.</p>', '2016-09-01'),
(22, 'УЗНАЙ СВОЙ КРАЙ', 'Мы много знаем о родном крае, о его истории и  достопримечательностях. \r\nНо одно дело знать теоретически и совсем другое – увидеть собственными \r\nглазами. С этой целью группа ветеранов первичной организации ветеранов \r\nаппарата Гомельского отделения железной дороги отправилась на экскурсию в\r\n древний город Туров. Располагается он всего в 25 км от железнодорожной \r\nстанции Житковичи, но бывали в нем очень немногие.  Увидеть своими \r\nглазами «растущий из-под земли» крест на древнем Борисоглебском кладбище\r\n действительно интересно. И посмотреть на это чудо и поклониться \r\nкаменному кресту в Туров приезжают паломники не только из Беларуси, но и\r\n из Украины и России. Большой интерес у экскурсантов вызвало также \r\nпосещение историко-краеведческого музея и Замковой горы.<br>\r\n				&nbsp;&nbsp;&nbsp;Кроме посещений исторических мест, по экскурсионной программе \r\nветераны побывали также на современном высокотехнологичном, оснащенном \r\nвысокопроизводительным оборудованием ведущих европейских компаний ОАО \r\n«Туровский молочный комбинат». Здесь ветераны смогли не только увидеть, \r\nкак производятся полутвердые и мягкие сыры, но и продегустировать \r\nпродукцию предприятия.<br>\r\n				&nbsp;&nbsp;&nbsp;В программу экскурсии было также включено посещение агрогородка \r\nЛясковичи, где неизгладимое впечатление произвел на ветеранов Музей \r\nприроды. Разнообразие и изобилие экспонатов поражали своей \r\nэкспозиционной инсценировкой и художественным исполнением. Закончилась \r\nпоездка приятной экскурсией на теплоходе. Обратная дорога домой была \r\nнаполнена оживленным обменом впечатлений.<br>\r\n				&nbsp;&nbsp;&nbsp;Ветераны  выражают благодарность председателю совета ветеранов \r\nаппарата Гомельского отделения железной дороги Маслюковой Н.П., \r\nпрофсоюзной организации  Гомельского отделения железной дороги и в \r\nчастности председателю профкома Житникову С.А., поддержавшему инициативу\r\n ветеранов и оказавшему содействие в организации и осуществлении \r\nэкскурсионной поездки.', '2016-09-03'),
(23, '2-3 сентября 2016 года прошел VI областной межотраслевой туристический слет профсоюзов Гомельщины', ' В спортивных соревнованиях приняли участие практически все \r\nотраслевые профсоюзные организации области. Всего 17 команд из более 600\r\n участников соревнований.<br><p>\r\n				\r\nНа торжественном открытии межотраслевого профсоюзного турслёта \r\nприсутствовали руководители областных структур образования, спорта и \r\nпрофсоюзов. Среди приглашенных на открытие турслёта присутствовали \r\nсеребряный призер Олимпийских Игр в Рио-де-Жанейро по вольной борьбе \r\nМария Мамошук и ее первый тренер Петр Павлович Бабей, тренирующий своих \r\nвоспитанников в СДЮШОР Гомельского вагоностроительного завода, \r\nучредителем которой является первичная профсоюзная организация \r\nпредприятия.<br>\r\n				&nbsp;&nbsp;&nbsp;В программу турслёта входили спортивные соревнования по 8 видам \r\nспорта: велотуризм, перетягивание каната (муж.), стрельба, туристическая\r\n полоса препятствий, прыжки со скакалкой, дартс, вис на перекладине, \r\nпляжный волейбол. Организовали и четыре творческих конкурса: метание \r\nяиц, конкурс на лучшее приготовление походного блюда, на лучший бивуак и\r\n музыкальный конкурс.<br>\r\n				&nbsp;&nbsp;&nbsp;В каждом виде спорта проводимого областного турслета предстояла \r\nбольшая конкуренция и добиться передовых позиций было очень непросто. По\r\n итогам соревнований наша команда была награждена кубками за второе \r\nместо по волейболу и конкурсе на лучшее приготовление походного блюда.</p>', '2016-09-06'),
(24, 'Вот так и живем', ' Завершилась пора летних отпусков и детских каникул. С приходом \nосени и многообразия осенних красок за окном профсоюзным комитетом \nГомельского вагонного участка РУП «Гомельское отделение Белорусской \nжелезной дороги», где 1261 член профсоюза, подводятся итоги летнего \nдетского оздоровления (110 детей работников предприятия оздоровилось в \nлагере «Лесные дали»), по проведению акции «Собери портфель \nпервокласснику» помощь оказана 30 многодетным семьям на сумму 900 \nрублей, уделено большое внимание рассмотрению письменных обращений \nработников и организации обучения общественных инспекторов, а также \nобсуждается подготовка к международному Дню пожилых людей, Дню матери.<br><p>\n				&nbsp;&nbsp;&nbsp;В соответствии с планом работы, учитывая проведение Года \nкультуры, в сентябре месяце профсоюзным комитетом для работников \nпредприятия и их детей приобретены билеты на разнообразные \nкультурно-развлекательные программы, фестиваль «Сожскi карагод», \nпредставления во Дворце культуры железнодорожников. По многочисленным \nпросьбам работников побывать на Туровской земле была организована \nпоездка с посещением Замковой горы, краеведческого музея, \nБорисоглебского кладбища, часовни.<br>\n				&nbsp;&nbsp;&nbsp;Ну и конечно неизгладимое впечатление оставил на участников тура \nфестиваль «Зов полесья». Прогуливаясь по импровизированной «Полесской \nдеревне», наши работники познакомились с этнокультурными традициями, \nпосвященными тому или иному обряду. Впервые в рамках «Зова Полесья» \nпрошел конкурс-фестиваль «Перезвон талантов», где участвовали юные \nремесленники. Народные песни, обряды, танцы, костюмы, угощения на \nподворьях – только в Лясковичах и только раз в два года можно \nприкоснуться к колориту и особенностям всей Полесской земли. Словом \nполучен неимоверный заряд позитива от концертной программы и лазерного \nшоу. Есть надежда, что проведя интересно и с пользой выходной день, \nвыходить на работу наши члены профсоюзной организации будут с хорошим \nнастроением. </p>', '2016-09-24'),
(25, 'Интересная и познавательная экскурсия в Мир и Несвиж.', 'В субботу, 8 октября 2016 г., первичной организацией профсоюза \r\nсотрудников учреждения образования «Белорусский государственный \r\nуниверситет транспорта» для членов профсоюза и их несовершеннолетних \r\nдетей при помощи ТЭУП «Беларустурист» была организована интересная и \r\nпознавательная поездка «Старажытныя замкi Беларусi». В поездке, \r\nпрошедшей по маршруту Гомель – Мир – Несвиж – Гомель, участвовал \r\nпрофессорско-преподавательский состав университета, а также \r\nучебно-вспомогательный персонал кафедр и деканатов, научные работники \r\nлабораторий Научно-исследовательского института железнодорожного \r\nтранспорта, работники научно-технической библиотеки БелГУТа, \r\nадминистративно-хозяйственной части, неработающие ветераны университета –\r\n всего 48 человек.<br><p>\r\n				&nbsp;&nbsp;&nbsp;Поездка была организована для ознакомления членов профсоюза и их \r\nнесовершеннолетних детей с такими историческими, архитектурными и \r\nкультурными шедеврами Беларуси, включенными в список Всемирного наследия\r\n ЮНЕСКО, как Мирский и Несвижский замки. С большим интересом члены \r\nпрофсоюза слушали рассказы экскурсоводов о создании и развитии замков в \r\nМире и Несвиже, об их славной и непростой истории, а также о судьбе их \r\nхозяев, представителей знаменитых родов ВКЛ-Беларуси Ильиничах, Кишках, \r\nРадзивиллах. А судьбы замков и их хозяев были, конечно, неразрывно \r\nсвязаны с историческими судьбами нашей Родины. Неувядаемую воинскую \r\nславу заслужили защитники Несвижского замка. Так, во времена \r\nнациональной трагедии Беларуси, при вторжении в 1654 – 1667 годах на \r\nтерриторию нашей страны армий московского царя Алексея Михайловича \r\n«Тишайшего», отца Петра I, Несвиж и Мир, как и абсолютное большинство \r\nгородов Беларуси, были сожжены и разграблены. Однако защитники \r\nНесвижского замка в 1655 и 1659 годах мужественно выдержали длительные, \r\nмногомесячные осады и не сдали крепость врагу. Участники экскурсии \r\nосмотрели дворцово-парковые ансамбли XVI – XIX вв., Несвижскую ратушу, \r\nзнаменитую Слуцкую браму. Экскурсии оставили яркие и незабываемые \r\nвпечатления.<br>\r\n				&nbsp;&nbsp;&nbsp;Экскурсионные поездки по историческим местам нашей Родины \r\nпозволяют членам профсоюза не только занимательно провести время и \r\nрасширить свой кругозор, но и воспитывают чувство гордости за нашу \r\nстрану, ее талантливый, трудолюбивый в мирное время и мужественный, \r\nсамоотверженный во времена военного лихолетья народ. </p>', '2016-10-08'),
(26, 'Чемпионат Мира по армрестлингу.', 'С 1 по 10 октября 2016 года в городе Благоевграде, Болгария, \r\nпроходил 38 чемпионат Мира по армрестлингу, в котором приняли участие \r\n1150 спортсменов из 52 стран. Сборную Республики Беларусь представляли \r\n14 спортсменов, Гомельскую область - воспитанник СДЮШОР ПОП ЗАО \r\n«Гомельский ВСЗ» Олег Макаренко и ведущий инженер отдела АСУП ЗАО \r\n«Гомельский ВСЗ» Татьяна Калюк. В весовой категории +80 кг среди женщин \r\nТатьяна Калюк заняла 4 место, среди мужчин в весовой категории 65 кг 4 \r\nместо занял Олег Макаренко.<br><p>\r\n				&nbsp;&nbsp;&nbsp;Татьяна Калюк: «…победа в схватке с многократной чемпионкой Мира и\r\n Европы, заслуженным мастером спорта России Валентиной Федосеевой в \r\nчетвертьфинале стала трамплином для выхода в финальную часть \r\nсоревнований. Обидное поражение в полуфинале от не менее сильной \r\nроссиянки, чемпионки России 2016г. Алины Саматой не позволило продолжить\r\n дальнейшую борьбу за медаль. Это значит есть над чем работать на пути к\r\n пьедесталу почета!»<br>\r\n				&nbsp;&nbsp;&nbsp;Многолетний опыт участия Татьяны Калюк в международных \r\nсоревнованиях и турнирах, Чемпионатах Европы и Мира по армрестлингу дает\r\n надежду на полное раскрытие ее таланта.</p>', '2016-10-10'),
(27, 'Вопросы государственной молодежной политики Беларуси решали в Дворце творчества детей и молодежи', '<b> Круглый стол на тему "Вклад детских и молодежных общественных \r\nорганизаций в реализацию государственной молодежной политики Беларуси".</b> <br>18 октября 2016 г. в областном Дворце творчества детей и молодежи при \r\nучастии Молодёжного Совета Гомельского областного объединения профсоюзов\r\n в лице председателя ППО студентов Белорусского государственного \r\nуниверситета транспорта Красник Анны состоялся круглый стол на тему \r\n"Вклад детских и молодежных общественных организаций в реализацию \r\nгосударственной молодежной политики Беларуси".<br>\r\n				&nbsp;&nbsp;&nbsp;В ходе мероприятия были вынесены на рассмотрение актуальные \r\nнаправления реализации государственной молодёжной политики Республики \r\nБеларусь. Так же участники круглого стола смогли ознакомиться с опытом \r\nработы детских молодёжных организаций Гомельской области таких, как \r\nЮНЕСКО, БРСМ, Белорусской республиканской пионерской организации, \r\nГомельского областного отделения ДОО ассоциации белорусских гайдов, и, \r\nконечно же, Молодёжного совета.<br>\r\n				&nbsp;&nbsp;&nbsp;Следует отметить что вопросы, вынесенные на обсуждение совместной деятельности, были очень важными и своевременными.', '2016-10-18'),
(28, 'Встреча с серебряным призером Олимпийских игр в Рио-2016 Марией Мамошук', '20 октября 2016г. состоялась встреча трудового коллектива ЗАО \n«Гомельский ВСЗ» с серебряным призером Олимпийских игр в Рио-2016 Марией\n Мамошук. В ходе встречи Мария поделилась планами на перспективу и \nохотно ответила на вопросы трудящихся."<br>\n				&nbsp;&nbsp;&nbsp;На встрече присутствовали директор учреждения «Специализированная\n детская юношеская спортивная школа олимпийского резерва » Первичной \nпрофсоюзной организации Закрытого акционерного общества «Гомельский \nвагоностроительный завод» Дрозд Олег Александрович и \nтренер-преподаватель по вольной борьбе, заслуженный тренер Республики \nБеларусь Бабей Петр Павлович. Марию пришли поздравить председатель \nНаблюдательного совета ЗАО «Гомельский ВСЗ» В.В. Балахонов, заместитель \nглавы Администрации Советского района г. Гомеля А.Н. Кулакевич, \nзаместитель начальника ОВД Администрации Советского района подполковник \nмилиции М.Н. Синельников. <br>\n				&nbsp;&nbsp;&nbsp;От администрации предприятия и профсоюзного комитета ППО ЗАО \n«Гомельский ВСЗ» почетным гостям Марии Мамошук, Олегу Дрозду и Петру \nБабею были вручены почетные грамоты ЗАО «Гомельский ВСЗ» и ценные \nподарки, в ходе встречи звучали теплые слова напутствия и \nпризнательности.', '2016-10-20'),
(29, '70 лет Гомельскому отделению Белорусской железной дороги', 'В соответствии с приказом Министра путей сообщения СССР от 23 \r\nсентября 1946 года было организовано 274 отделения дороги, в том числе и\r\n Гомельское отделение дороги.<br>\r\n				В честь знаменательной даты, 70-летия образования Гомельского \r\nотделения дороги, 11 ноября 2016 года во Дворце культуры \r\nжелезнодорожников им. Ленина состоялись торжественное собрание и \r\nпраздничный концерт.<br>\r\n				&nbsp;&nbsp;&nbsp;В заполненном концертном зале на 1100 мест присутствовали \r\nработники всех предприятий и профессий Гомельского отделения дороги, \r\nветераны труда и профсоюзного движения, представители от районных \r\nадминистраций города, городской епархии, руководители многих предприятий\r\n города и области.<br>\r\n				&nbsp;&nbsp;&nbsp;Более 60 железнодорожников были награждены Почётными грамотами и \r\nБлагодарностями Гомельского отделения дороги и Объединенной районной \r\nпрофсоюзной организации, которые были вручены начальником отделения \r\nДвораком Г.В. и председателем Райпрофсожа Сущинским В.В.<br>\r\n				&nbsp;&nbsp;&nbsp;Также каждому присутствующему на торжественном мероприятии был вручен подарочный набор.<br>\r\n				&nbsp;&nbsp;&nbsp;Работниками Дворца культуры были подготовлены и выставлены стенды\r\n об историческом 70-летнем пути строительства и совершенствования \r\nотделения дороги, экспозиция картин, вышивок и поделок \r\nветеранов-железнодорожников и многое другое.<br>\r\n				&nbsp;&nbsp;&nbsp;В фойе Дворца культуры была организована экспозиция плакатов \r\nпервичных профсоюзных организаций под девизом «Мы – вместе! 70 лет \r\nГомельскому отделению дороги», на которых размещалась информация о \r\nдеятельности профсоюзных организаций.', '2016-11-15'),
(30, 'В БелГУТе прошла межфакультетская интеллектуальная игра «Что? Где? Когда?»', 'В четверг, 17 ноября 2016 г., в Белорусском государственном \nуниверситете транспорта в честь 73-й годовщины освобождения г. Гомеля от\n немецко-фашистских захватчиков прошла межфакультетская интеллектуальная\n игра «Что? Где? Когда?». Ведущим и одним из основных организаторов игры\n был старший преподаватель, заместитель декана заочного факультета А.Н. \nПолевой – руководитель собственной команды и неоднократный участник \nинтеллектуальной телеигры «Что? Где? Когда? в Беларуси», выходящей по \nвыходным дням в эфире телеканала ОНТ.<br><br>&nbsp;&nbsp; За победу в интеллектуальной игре и сладкие призы первичной \nорганизации профсоюза сотрудников УО «БелГУТ» соревновались команды 8 \nфакультетов университета – механического, строительного, промышленного и\n гражданского строительства, военно-транспортного, управления процессами\n перевозок, гуманитарно-экономического, электротехнического и факультета\n иностранных студентов. Необходимо отметить, что турниры по игре «Что? \nГде? Когда?» проводятся в БелГУТе регулярно и пользуются большой \nпопулярностью у профессорско-преподавательского состава и сотрудников \nуниверситета. Не так просто попасть в число членов команды факультета. \nУчастник игры должен не только обладать обширными, практически \nэнциклопедическими знаниями по всемирной истории, культуре, литературе, \nестествознанию и современным технологиям и т. д., но и иметь быстрое и \nлогическое мышление – на обдумывание ответа на сложные и нарочито \nзапутанные вопросы дается не более одной минуты! <br><br>\n                                 &nbsp;&nbsp;&nbsp;Игра 17 ноября проходила, как, \nвпрочем, и всегда, в интересной, острой, азартной и бескомпромиссной \nборьбе. То одна, то другая команда вырывались в лидеры и затем теряли \nзавоеванные позиции, а для определения победителя пришлось вводить \nдополнительные показатели и учитывать распределение мест в \nпредварительном «разминочном» конкурсе «Своя игра». В итоге победителем \n(и уже далеко не в первый раз!) стала команда электротехнического \nфакультета, получившая за свою победу в качестве сладкого приза 3 торта \nот профсоюзного комитета и специальный приз от ведущего игры А.Н. \nПолевого. Вот состав команды победителей: капитан команды, доцент \nкафедры «Системы передачи информации Буй П.М., доцент кафедры «Высшая \nматематика» Дудко С.А., старший преподаватель кафедры «Микропроцессорная\n техника и информационно-управляющие системы» Измайлов К.Ф., заместитель\n декана электротехнического факультета Волынец В.В., лаборант кафедры \n«Электротехника» Мельникова З.Н., ассистент кафедры «Электротехника» \nГатальская И.А.<br><br>На втором месте с минимальным отрывом от победителя оказалась команда \nмеханического факультета, возглавляемая доцентом кафедры «Вагоны» \nЧерниным Р.И. За красивую и бескомпромиссную борьбу и высокие результаты\n команда механического факультета также получила три торта в качестве \nутешительного сладкого приза от профсоюзного комитета. Заняла третье \nместо и получила в награду торт от профкома команда \nгуманитарно-экономического факультета, капитаном которой в прошедшей \nигре был доцент кафедры «Философия, история и политология» Бессольнов \nА.Б.<br><br>&nbsp; Все другие участвовавшие в игре «Что? Где? Когда?» 17 ноября 2016 г. \nкоманды показали достаточно высокие результаты, и соревнуйся они не с \nтакими «интеллектуальными монстрами», как команды электротехников и \nмехаников, также могли бы претендовать на самые высокие места. \nНеобходимо отметить, что уровень команд, не смотря на регулярную ротацию\n состава, с каждым годом заметно растет, а сами игры проходят во все \nболее интересной и острой борьбе.<br>', '2016-11-17'),
(31, 'Подведение итогов круглогодичной спартакиады.', 'В Культурно-спортивном комплексе РУП «Гомельское отделение \nБелорусской железной дороги» 21 декабря прошло подведение итогов \nКруглогодичной Спартакиады РУП "Гомельское отделение Белорусской \nжелезной дороги" 2016 года. <br>\n				\n			\n                        <p> &nbsp;&nbsp;&nbsp;В ходе упорной борьбы первое место занял \nколлектив ЗАО «Гомельский вагоностроительный завод», второе – коллектив \nГомельской дистанции сигнализации и связи, третье – Гомельского \nлокомотивного депо.<br>\n                        </p>\n                        &nbsp;&nbsp;&nbsp;Победителям и призерам были вручены дипломы и ценные подарки соответствующих степеней.', '2016-12-21'),
(32, 'С поздравлениями в детский дом!', 'С особой теплотой накануне Нового 2017 года встретили делегацию от \nГомельского отделения железной дороги воспитанники и преподаватели \nГомельского городского детского дома. <br>\n			\n                        \n                        <p>\n			&nbsp;&nbsp;&nbsp;И это не удивительно. Ведь за последние годы предновогодние \nвстречи воспитанников детского дома с представителями рабочей молодежи и\n Объединенной районной профсоюзной организации железнодорожников стали \nтрадиционными. <br>\n			</p>\n                        \n                        <p>\n			&nbsp;&nbsp;&nbsp;Кроме тёплых слов и пожеланий к Новому году от администрации \nотделения дороги были перечислены финансовые средства на учебные, \nразвлекательные и хозяйственные нужды детского дома. От профсоюзной \nорганизации дети получили сладкие новогодние подарки. <br>\n			</p>\n                        \n                        \n			&nbsp;&nbsp;&nbsp;Красочное и веселое новогоднее представление воспитанников \nдетского дома для присутствующих в зале добавило праздничность \nпроводимого мероприятия.', '2016-12-27'),
(33, 'Рождественский турнир!', 'В Культурно-спортивном комплексе РУП «Гомельское отделение \nБелорусской железной дороги» 06.01.2017 года при массовой поддержке \nболельщиков был проведен традиционный Рождественский турнир по волейболу\n среди команд НОДа собственного Гомельского отделения Белорусской \nжелезной дороги. <br>\n			\n                        \n                        \n			&nbsp;&nbsp;&nbsp;Соревнования проводились с целью дальнейшей популяризации \nволейбола и привлечения к занятиям физической культурой и спортом \nжелезнодорожников, пропаганды здорового образа жизни на отделении \nдороги.', '2017-01-06'),
(34, 'Семинар-учеба профактива Гомельского Райпрофсожа', ' 24 февраля 2017 года во Дворце культуры железнодорожников был \nпроведен семинар-учеба профактива Объединенной районной профсоюзной \nорганизации РУП «Гомельское отделение Белорусской  железной дороги». <br>\n			\n                        \n            <p>\n			&nbsp;&nbsp;&nbsp;Широкая программа лекций в плане работы семинара по направлениям \nуставной деятельности профсоюзных организаций была представлена \nслушателям специалистами БелПрофТранса, Дорпрофсожа, Гомельского \nобластного объединения профсоюзов, Гомельского областного управления \nДепартамента государственной инспекции труда Министерства труда и \nсоциальной защиты, Гомельского отдела УО ФПБ «Международный университет \n«МИТСО», Райпрофсожа. <br>\n			</p>\n                        \n            <p>\n			&nbsp;&nbsp;&nbsp;Так же по теме семинара «Социальное партнерство. Содействие в \nработе при выполнении производственных и общественных заданий \nспециалистами идеологического сектора и профсоюзных организаций \nГомельского отделения дороги» выступил заместитель начальника \nГомельского отделения дороги, член президиума Райпрофсожа Каймович В.Д. <br>\n			</p>\n                        \n            \n			&nbsp;&nbsp;&nbsp;Большая подборка раздаточного материала по профсоюзной \nдеятельности и решений вышестоящих профсоюзных организаций была \nпредставлена председателям профсоюзных комитетов от представителей \nБелПрофТранса, Дорпрофсожа и Райпрофсожа.', '2017-02-24');
INSERT INTO `news` (`id`, `title`, `text`, `date`) VALUES
(35, 'С наступающим праздником, дорогие женщины!', 'Накануне международного женского дня во Дворце культуры \r\nжелезнодорожников состоялось торжественное мероприятие к международному \r\nженскому дню - 8 Марта. <br>\r\n			\r\n                        \r\n            <p>\r\n			Организатором мероприятия – Гомельским областным объединением \r\nпрофсоюзов на торжественное чествование были приглашены более 70 \r\nпредставительниц прекрасного пола, пять из которых представители от \r\nОбъединенной районной профсоюзной организации РУП «Гомельское отделение \r\nБелорусской железной дороги». Это: Корхова Ирина Леонидовна - главный \r\nбухгалтер Райпрофсожа; Ибрагимова Антонина Олеговна – ведущий специалист\r\n Райпрофсожа; Шоломицкая Ольга Анатольевна – председатель ППО вокзала \r\nстанции Гомель; Лавринович Елена Викторовна – председатель ППО Путевой \r\nмашинной станции №116; Малахова Светлана Владимировна – заместитель \r\nглавного бухгалтера, казначей ППО Жлобинского вагонного депо. <br>\r\n			</p>\r\n                        \r\n            <p>\r\n			&nbsp;&nbsp;&nbsp;Свои поздравления к празднику и слова благодарности за выбранную \r\nактивную позицию в жизни, на производстве, в общественной деятельности \r\nвысказали председатель  Гомельского областного объединения профсоюзов \r\nА.Н.Неверов, председатели областных комитетов профсоюзов. От \r\nобъединенной районной профсоюзной организации РУП «Гомельское отделение \r\nБелорусской железной дороги» присутствующих женщин поздравил \r\nпредседатель Райпрофсожа В.В.Сущинский. <br>\r\n			</p>\r\n                        \r\n            \r\n			&nbsp;&nbsp;&nbsp;Интересная музыкальная программа поздравлений была подготовлена \r\nсилами Культурно-спортивного комплекса Гомельского отделения железной \r\nдороги.', '2017-03-07'),
(36, 'Благотворительная помощь детям!', ' Накануне годовщины катастрофы на Чернобыльской АЭС председатель \nОбъединенной районной профсоюзной организации РУП «Гомельское отделение \nБелорусской железной дороги» Сущинский В.В., в соответствии с решением \nОбъединенной отраслевой профсоюзной организации железнодорожников и \nтранспортных строителей «О благотворительной акции, приуроченной к \nгодовщине катастрофы на Чернобыльской АЭС», принимая во внимание \nсложившуюся практику оказания помощи подшефным организациям, посетил \n«Санаторный ясли-сад №3 г.Наровли». <br>\n			\n                        \n            <p>\n			&nbsp;&nbsp;&nbsp;В ходе подготовки к посещению ясли-сада, в виде благотворительной \nпомощи из финансовых средств Дорпрофсожа были приобретены 13 детских \nкроваток и принтер <br>\n			</p>\n                        \n            \n			&nbsp;&nbsp;&nbsp;Выражая огромную признательность за участие в решении некоторых из\n многочисленных проблем детского заведения, от коллектива сотрудников \nгосударственного учреждения образования«Санаторный ясли-сад №3 \nг.Наровли» поступило Благодарственное письмо в адрес профсоюзов.', '2017-04-25'),
(37, 'Чествование победителей областного конкурса «Гомельские мастера-2017»', '<STRONG>4 мая на базе Областного объединения профсоюзов состоялось чествование победителей областного конкурса «Гомельские мастера-2017». Среди участников данного мероприятия победители и призеры областных и республиканских конкурсов профессионального мастерства по наиболее массовым профессиям.</STRONG> <BR>\r\n<P>&nbsp;&nbsp;&nbsp;Всего представлено 22 организации и 56 специальностей, среди которых от Гомельского отделения железной дороги были представители таких профессий, как дежурный по станции, машинист, проводник пассажирского вагона, электромеханик связи, монтер пути, билетный кассир.<BR></P>&nbsp;&nbsp;&nbsp;Основная цель конкурса – повышение престижа человека труда, активизация деятельности трудовых коллективов и профсоюзных организаций в области пропаганды достижений передового опыта. Победители конкурса награждены нагрудными знаками «Гомельский мастер-2017», дипломами и денежными премиями. Мероприятие завершилось праздничным концертом', '2017-05-04'),
(39, 'Достойный результат на чемпионате Европы', 'В период с 13.05.2017 по 21.05.2017 г. в городе Катовице (Польша) \nпрошёл чемпионат Европы по армрестлингу, в котором приняли участие 940 \nспортсменов из 38 стран. СДЮШОР ППО ЗАО «Гомельский ВСЗ» представляли – \nТатьяна Калюк, Лариса Шаповалова и Олег Макаренко. <br>\n			\n                        \n            <p>\n			&nbsp;&nbsp;&nbsp;Спортсмены показали достойный результат на чемпионате Европы по \nармрестлингу среди женщин:\n			ТАТЬЯНА КАЛЮК - бронзовая медаль в весовой категории +80 кг, ЛАРИСА \nШАПОВАЛОВА - серебряная и бронзовая медаль в весовой категории 50 кг.\n			<br>\n			</p>\n                        \n            \n			&nbsp;&nbsp;&nbsp;Председатель ППО ЗАО «Гомельский ВСЗ»  Езерская С.В.', '2017-05-26'),
(40, 'Выездной президиум Райпрофсожа', '23 мая 2017 года в г.Калинковичи  состоялось выездное заседание  \nпрезидиума Райпрофсожа РУП «Гомельское отделение Белорусской железной \nдороги». В ходе работы были рассмотрены вопросы: «О ходе подготовки \nоздоровительных учреждений Санатория РУП «Гомельское отделение \nБелорусской железной дороги» к работе и оздоровлению детей \nжелезнодорожников в летний период 2017 года», «О выполнении основных \nнаправлений работы по укреплению общественной безопасности и дисциплины в\n свете требований Директивы №1 и Декрета №5 Президента Республики \nБеларусь в организациях, объединяемых Райпрофсожем Гомельского отделения\n дороги» и прочие вопросы. <br>\n			\n                        \n            <p>\n			&nbsp;&nbsp;&nbsp;После заседания президиума присутствующие побывали и глубже \nознакомились с работой железнодорожных предприятий Калинковичского узла.\n			<br>\n			</p>\n                        \n            \n			&nbsp;&nbsp;&nbsp;Кроме того с целью познания достопримечательностей  родного края \nбыла организована экскурсия в уникальный туристический комплекс деревни \nЮровичи Калинковичского района, на территории которого шесть тысяч лет \nдо н.э. была стоянка первобытного человека и мужской монастырь на \nтерритории Храма Рождества Пресвятой Богородицы.', '2017-05-27'),
(41, 'Информационный день БелПрофТранса в Гомеле', ' В соответствии с планом работы Белорусского профессионального союза\n работников транспорта и коммуникаций, 07.06.2017г. в Гомеле проводился \nинформационный день БелПрофТранса. <br>\n			\n                        \n            <p>\n			&nbsp;&nbsp;&nbsp;Руководителями и специалистами Республиканского комитета профсоюза\n посещались первичные профсоюзные организации предприятий и организаций \nжелезнодорожного, авиационного, автомобильного, водного транспорта и \nдорожного хозяйства. <br>\n			</p>\n                        \n            \n			&nbsp;&nbsp;&nbsp;В завершении проведения информационного дня во Дворце \nжелезнодорожников представителями БелПрофТранса с профактивом \nотраслевого профсоюза были подведены итоги проделанной работы.', '2017-06-07'),
(42, 'Выездной президиум Райпрофсожа', '27 июня  2017 года на базе Жлобинской дистанции пути  состоялось \nвыездное заседание  президиума Райпрофсожа РУП «Гомельское отделение \nБелорусской железной дороги». В ходе работы были рассмотрены вопросы: «О\n результатах выполнения постановления президиума Дорпрофсожа «О \nсоблюдении режима труда и отдыха локомотивных бригад», «О ходе \nреализации планов первичных профсоюзных организаций по выполнению \nкомплекса мер социально-экономического развития Республики Беларусь на \n2016-2020 годы» и прочие вопросы. В работе президиума принял участие \nглавный технический инспектор Дорпрофсожа Новик А.И.<br>\n			\n                        \n            <p>\n			&nbsp;&nbsp;&nbsp;В план работы президиума также было включено ознакомление \nучастников с работой железнодорожных предприятий Жлобинского узла.<br>\n			</p>\n                        \n            \n			&nbsp;&nbsp;&nbsp;Кроме этого с целью познания достопримечательностей  родного края \nбыла организована экскурсия в Мемориальный комплекс «Красный берег» на \nместе детского концлагеря и старинную усадьбу Козел-Поклёвских в г/п \nКрасный берег. Исторические места не оставили равнодушных среди \nучастников экскурсий.', '2017-06-27'),
(43, 'Социальное партнерство в уборочной компании', '&nbsp;&nbsp; Первые страницы республиканских и местных областных газет августа \r\nмесяца традиционно пестрят новостями о сборе урожая, начиная от \r\nгосударственных предприятий до приусадебных участков.<br>\r\n			\r\n                        \r\n            <p>\r\n			&nbsp;&nbsp;&nbsp;Оставляет свой след горячий «Жнiвень» и в деятельности профсоюзов.<br>\r\n			</p>\r\n                        \r\n            <p>\r\n			&nbsp;&nbsp;&nbsp;В соответствии с рекомендациями Совета ФПБ и Республиканского \r\nкомитета отраслевого профсоюза специалистами Объединенной районной \r\nпрофсоюзной организации РУП «Гомельское отделение Белорусской железной \r\nдороги» в этот период осуществляется мониторинг по организации \r\nпроведения уборочной компании в сельскохозяйственных предприятиях \r\nКалинковичского, Речицкого и Лельчицкого  районов Гомельской области.<br>\r\n			</p>\r\n\r\n            <p>\r\n			&nbsp;&nbsp;&nbsp;В очередной раз группа представителей Гомельского отделения дороги\r\n в составе председателя Райпрофсожа Сущинского В.В., заместителя \r\nначальника отделения дороги Ходькова С.В. и заместителя председателя  \r\nсовета ветеранов отделения дороги Шилова Б.Б. посетили СХДУП \r\n«Крынки-Агро».<br>\r\n			</p>\r\n\r\n            <p>\r\n			&nbsp;&nbsp;&nbsp;По информации руководителя предприятия «Крынки-Агро» Близнеца \r\nВ.Ф., хозяйство завершило уборочную компанию зерновых, что составило \r\nболее 900 (га) площадей, при этом средняя урожайность составила 30 \r\nцентнеров с гектара при средней бальности земли 20 баллов.<br>\r\n			</p>\r\n\r\n            <p>\r\n			&nbsp;&nbsp;&nbsp;Также рассказал, что уборочную сельскохозяйственную технику к \r\nсезону готовят заранее и доверяют ее лучшим и ответственным специалистам\r\n таким, как:<br></p><ul><li>\r\n			&nbsp;&nbsp;&nbsp;Коцур Анатолий Леонидович – комбайнер (КЗС – 1218)</li><li>\r\n			&nbsp;&nbsp;&nbsp;Руденко Дмитрий Алексеевич – помощник комбайнера </li><li>\r\n			&nbsp;&nbsp;&nbsp;Дегтяренко Сергей Михайлович – комбайнер (КЗС – 10К) </li><li>\r\n			&nbsp;&nbsp;&nbsp;Хохол Сергей Иванович – помощник  комбайнера. </li><li>\r\n			<br></li></ul>\r\n\r\n            <p>\r\n			&nbsp;&nbsp;&nbsp;За время уборочной компании 2017 года первый экипаж намолотил 772,7 тонны зерна, второй – 510,3 тонны.<br>\r\n			</p>\r\n\r\n            <p>\r\n			&nbsp;&nbsp;&nbsp;Все указанные люди работают трактористами – машинистами сельскохозяйственного производства на предприятии не один год.<br>\r\n			</p>\r\n\r\n            <p>\r\n			&nbsp;&nbsp;&nbsp;В свою очередь комиссией отделения дороги было отмечено, что в \r\nпоследние годы в хозяйстве возросло качество содержания \r\nсельскохозяйственной техники и производственных комплексов, улучшена \r\nэффективность сельскохозяйственных работ.<br>\r\n			</p>\r\n\r\n            \r\n			&nbsp;&nbsp;&nbsp;В процессе посещения с/х «Крынки-Агро» представители комиссии \r\nвручили от Объединенной районной профсоюзной организации отличившимся \r\nвысокими показателями в работе специалистам ценные подарки в виде \r\nмультиварок и кухонных комбайнов.', '2017-08-18'),
(44, 'Завершился сезон детского оздоровления', ' В летний период 2017 года на Гомельском отделении железной дороги \nбыла организована работа оздоровительного лагеря «Лесные дали» с \nкруглосуточным пребыванием детей и 17 августа завершился летний сезон.<br>\n			\n                        \n            <p>\n			&nbsp;&nbsp;&nbsp;Всего за три организованных заезда 2017 года в лагере было \nоздоровлено 877 детей работников предприятий и организаций Гомельского \nотделения железной дороги.<br>\n			</p>\n                        \n            <p>\n			&nbsp;&nbsp;&nbsp;В этом году детское оздоровление проходило под республиканской \nакцией «Лето – время открытий». Скучать маленьким отдыхающим не \nприходилось, ведь в течение отдыха было много спортивных и \nкультурно-развлекательных мероприятий: конкурсы, викторины, репетиции, \nсоревнования по волейболу, езда на велосипедах, рисование мелками, \nзанятия рукоделием. Для работы были привлечены работники, имеющие опыт \nработы и соответствующее образование.<br>\n			</p>\n\n            <p>\n			&nbsp;&nbsp;&nbsp;Особое внимание как со стороны администрации, так и профсоюзов \nуделялось контролю за организацией питания детей, условиями пребывания и\n досуга.<br>\n			</p>\n\n            <p>\n			&nbsp;&nbsp;&nbsp;Значительный вклад в организацию работы по оздоровлению школьников\n внесли первичные профсоюзные организации, входящие в структуру \nГомельского Райпрофсожа, включая подготовку оздоровительных объектов, \nсодействие в приобретении путевок на оздоровление, доставку детей к \nместу отдыха. Члены профсоюза принимали активное участие в уборке \nтерритории, ремонту и благоустройству оздоровительного лагеря.<br>\n			</p>\n\n            <p>\n			&nbsp;&nbsp;&nbsp;Дети обеспечивались пятиразовым питанием, квалифицированной \nмедицинской помощью, интересной развлекательной программой.       Время \nотдыха пролетело незаметно. Ведь постоянно мальчишки и девчонки узнавали\n для себя что-то новое и интересное. Экскурсии, походы, спортивные \nсоревнования дали детям заряд бодрости на весь следующий учебный год.<br>\n			</p>\n\n            \n			&nbsp;&nbsp;&nbsp;В целом следует отметить, что слаженная совместная работа всех \nзаинтересованных структур и организаций Гомельского отделения железной \nдороги позволила успешно и без чрезвычайных происшествий провести летнее\n оздоровление.', '2017-08-21'),
(45, 'День знаний', 'Ответственной датой в организационной деятельности профсоюзных \r\nорганизаций железнодорожников всех уровней традиционно остается День \r\nзнаний.<br>По профсоюзной благотворительной акции «Соберём портфель \r\nпервокласснику» первичными профсоюзными организациями Гомельского \r\nРайпрофсожа была оказана помощь: детям-первоклассникам; на подготовку \r\nдетей из многодетных, неполных и опекунских семей; семей, имеющих детей \r\nинвалидов; приобретены билеты на тематическую программу «Весёлый звонок»\r\n и спектакль «Ледниковый период» на общую сумму более 33 тысяч \r\nбелорусских рублей.<br>\r\n			<p></p>\r\n                        \r\n            <p>\r\n			От студентов БелГУТа пятнадцати первоклассникам – детям из \r\nмногодетных семей, которые являются учениками ГУО «Детский сад-начальная\r\n школа №63» г.Гомеля, - была оказана помощь в сборе школьных \r\nпринадлежностей, а их одноклассникам – сладкие подарки и веселая \r\nконцертная программа.<br>\r\n			</p>\r\n\r\n            <p>\r\n			&nbsp;&nbsp;&nbsp;Массовые колонны учащихся и преподавателей были выстроены в День \r\nзнаний в учебных заведениях железнодорожной направленности - УО \r\n«Белорусский государственный университет транспорта», Гомельском \r\nколледже-филиале УО «БелГУТ», УО «Гомельский государственный \r\nпрофессиональный лицей железнодорожного транспорта».<br>\r\n			</p>\r\n\r\n            \r\n			&nbsp;&nbsp;&nbsp;Наряду с представителями администрации учебных заведений, \r\nуправления и отделения железной дороги, предприятий железнодорожного \r\nузла, ветеранами и прочими приглашенными, теплыми словами с наилучшими \r\nпожеланиями и напутствиями поздравили присутствующих председатель \r\nБелПрофТранса В.И. Ринг и председатель Райпрофсожа В.В. Сущинский.', '2017-09-05'),
(46, 'VIII областная межотраслевая спартакиада профсоюзов', ' Спортивная команда от Райпрофсожа РУП «Гомельское отделение \nБелорусской железной дороги» 15.09.2017 года приняла участие в VIII \nобластной межотраслевой спартакиаде профсоюзов Гомельской области, \nкоторая проводилась в г.Речица.<br>\n			\n                        \n            <p>\n			&nbsp;&nbsp;&nbsp;Из 17 команд, принявших участие в спартакиаде, команда Гомельских железнодорожников заняла 6-е общекомандное место.<br>\n			</p>\n                        \n            \n			&nbsp;&nbsp;&nbsp;Лучшими из проводимых видов спорта в команде Райпрофсожа по \nрезультатам выступлений оказалась команда волейболистов (4+2), которая в\n своем виде спорта заняла 2-е место.', '2017-09-15'),
(47, 'День города', 'Ярким и красочным перед жителями Гомельщины и его гостей выдался \r\n16 сентября 2017 года – День города, которому исполнилось 875 лет.<br>\r\n			\r\n                        \r\n            <p>\r\n			В городских праздничных мероприятиях, в составе Гомельского \r\nобластного объединения профсоюзов приняли участие и Гомельские \r\nжелезнодорожники.<br>\r\n			</p>\r\n\r\n            \r\n			&nbsp;&nbsp;&nbsp;В проводимой профсоюзной акции «Мы – вместе» от железнодорожников \r\nбыла представлена династия Хрупкиных-Самодум, которая составляет 12 \r\nчеловек (4 поколения), а трудовой стаж династии на железной дороге – 264\r\n года.', '2017-11-01'),
(48, '39 Чемпионат мира по армрестлингу в Венгрии, г. Будапешт с 02-11.09.2017', 'В ХХХIХ Чемпионате мира по армрестлингу, проходившему в г. \r\nБудапеште (Венгрия), с 02-11.09.2017 приняли участие 55 стран мира. \r\nСборную команду Республики Беларусь под руководством главного тренера \r\nДрозда Олега Александровича (директор СДЮШОР ППО ЗАО «Гомельский ВСЗ») \r\nпредставляли 17 спортсменов, в том числе спортсмены-учащиеся СДЮШОР ППО \r\nЗАО «Гомельский ВСЗ» Шаповалова Лариса, Веренич Иван, Цыкунова Валерия, \r\nПавлюченко Никита, Калюк Татьяна (ведущий инженер ОАСУП ЗАО «Гомельский \r\nВСЗ»). В напряженной борьбе наши спортсмены достигли высоких \r\nрезультатов. Среди женщин: Калюк Татьяна стала серебряным призером в \r\nвесовой категории свыше 80 кг. и выполнила норматив мастера спорта \r\nРеспублики Беларусь международного класса (МСМК); Шаповалова Лариса \r\nзавоевала серебряную и бронзовую медали в весовой категории до 60 кг. \r\nСреди юниоров и юниорок места распределились следующим образом: Веренич \r\nИван – 3 место в весовой категории до 75 кг., Цыкунова Валерия – 4 место\r\n в весовой категории до 50 кг., Павлюченко Никита – 6 место в весовой \r\nкатегории свыше 80 кг.<br>\r\n			\r\n\r\n            \r\n			&nbsp;&nbsp;&nbsp;Администрация и первичная профсоюзная организация ЗАО «Гомельский \r\nВСЗ» выражает благодарность Управлению спорта и туризма Гомельского \r\nобластного исполнительного комитета, РАЙПРОФСОЖУ РУП «Гомельское \r\nотделение Белорусской железной дороги», ОАО «Птицефабрика «Рассвет», \r\nИгровому клубу «Кайман» ОАО «Пирамида азарта» за поддержку в организации\r\n подготовки спортсменов СДЮШОР в ХХХIХ Чемпионате мира по армрестлингу.', '2017-09-20');
